import express from 'express'
import mongoose from "mongoose"
import queryRoutes from "./router/query.routes";
import articleRoutes from "./router/article.routes";
import commentRoutes from "./router/comment.routes";
import authRoutes from "./router/auth.routes";
import adminRoutes from "./router/admin.routes";
import {upload} from "./utils/file-uploader";
import passport from "passport"
import {jwtStrategy} from "./config/passport";

const port = process.env.APP_PORT || 5000
const app = express()
const database = process.env.DB_TEST_DATABASE
// const database = process.env.APP_ENV == "test" ? process.env.DB_TEST_DATABASE : process.env.DB_DATABASE
mongoose.connect(`${process.env.DB_CONNECTION_URL}/${database}`, {useNewUrlParser: true})
    .then(() => {
        app.use(express.json())
        app.use(upload.single("image"))
        app.use("/api/query", queryRoutes)
        app.use("/api/article", articleRoutes)
        app.use("/api/comment", commentRoutes)
        app.use("/api/auth", authRoutes)
        app.use("/api/admin", adminRoutes)
        app.use(express.static('storage'))
        app.use(express.static('public'))
        passport.use(jwtStrategy)
        app.use(passport.initialize());
        app.listen(port, () => {
            console.log(`Server started at ${process.env.APP_URL}:${port}`)
        })
    })
export default app

import express from "express"
import QueryController from "../controllers/QueryController";
import {isAuth} from "../middleware/is-auth";
import passport from "passport";

const router = express.Router()
router.get("/", isAuth(passport),QueryController.index)
 router.post("/", QueryController.store)
export default router

import Query from "../models/Query";
import {NewError} from "../utils/errors";
import {JsonResponse} from "../utils/response";
import queryValidation from "../validations/query.validator";

class QueryController {
    static async index(req, res) {
        const queries = await Query.find()
        return JsonResponse(res, "Listing queries!", queries)
    }

    static async store(req, res) {
        const {error} = queryValidation(req.body);

        if (error) {
            return NewError(res, 422, error.details[0].message)
        }
        const query = new Query({
            name: req.body.name,
            email: req.body.email,
            content: req.body.content,
        })
        await query.save()
        return JsonResponse(res, "Query Created!", query, 201)
    }
}

export default QueryController

import mongoose from 'mongoose'

const Schema = mongoose.Schema({
    name: {type: String, required: true},
    email: {type: String, required: true},
    content: {type: String, required: true},
    createdAt: {type: Date, default: Date.now},
})

export default mongoose.model("Query", Schema)

export const NewError = (res,status,message) => {
       return  res.status(status).send({message})
}

export const JsonResponse = (res, message, data, status = 200) => {
    return res.status(status).send({message, data})
}

import joi from 'joi'

const queryValidation =  (body) => {
    const querySchema = joi.object({
        name: joi.string().required().min(3).max(20),
        email: joi.string().email().required(),
        content: joi.string().required().min(10),
    })
    return querySchema.validate(body)
}
export default queryValidation

export const isAuth = (passport) => {
    return  passport.authenticate("jwt", {session: false})
};

import express from "express"
